import React, { useState, setState } from "react";
import "./WrapperBack.css";
import Card from "./Card";
import Bottom from "./Bottom";
import data from "../data.json";
import {
  FaCaretDown,
  FaCaretUp,
  FaCaretLeft,
  FaCaretRight,
} from "react-icons/fa";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";

const wstyles = {
  height: "100vh",
  width: "100vw",
  position: "relative",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  overflow: "hidden",
  flexDirection: "column",
};

const cstyles = {
  position: "relative",
};

const WrapperBack = () => {
  const [visible, setVisible] = useState(false);
  const [zoom, setZoom] = useState(1)

  const feedback = (e) => {
    console.log("zoom level");
    if (e.state.scale > 0.7) {
      setVisible((visible) => true);
    } else {
      setVisible((visible) => false);
    }

    setZoom(e.state.scale)
    console.log(e.state.scale);
  };

  return (
    <>
      <TransformWrapper
        initialScale={0.1}
        minScale={0.1}
        maxScale={100}
        initialPositionX={0}
        initialPositionY={0}
        onZoom={(e) => feedback(e)}
      >
        {({ zoomIn, zoomOut, resetTransform, ...rest }) => (
          <React.Fragment>
            <div className="tools">
              <button onClick={() => zoomIn()}>+</button>
              <button onClick={() => zoomOut()}>-</button>
              <button onClick={() => resetTransform()}>x</button>
            </div>
            <div className="div-wrapper">
              <TransformComponent>
                <div id="bg" className="outer-wrap back">
                  <img className="pic" style={{top: "2000-1200"}} src="./shoes/49.jpg" />
                  <img className="pic" src="./shoes/51.jpg" />
                  <img className="pic" src="./shoes/53.jpg" />
                  <img className="pic" src="./shoes/54.jpg" />
                </div>
              </TransformComponent>
            </div>
          </React.Fragment>
        )}
      </TransformWrapper>
      <button onClick={() => setVisible((visible) => !visible)}>YOOOOO</button>
    </>
  );
};

export default WrapperBack;

