import React, { useState, setState } from "react";
import { PanZoom } from "react-easy-panzoom";
import styled from "styled-components";
import { data } from "./data";

// const Container = styled.div`
//   height: 550px;
//   width: 300px;
//   display: flex;
//   flex-direction: row;
//   flex-wrap: wrap;
//   align-content: flex-start;
//   overflow: hidden;
//   border: 10px solid green;
//   background: blue;
// `;

const Container = styled.div`
  height: 550px;
  width: 300px;
  overflow: hidden;
  background: white;
  border: 10px solid green;
`;

const Wrapper = styled.div`
  // max-width: 200px;
  // overflow: visible;
  // display: block;
  // border: 10px solid red;
  // height: 800px;
  // width: 800px;
`;

const Map = styled.div`
  width: 2000px;
  height: 2000px;
  background-color: blue;
  border: 10px solid red;
  position: relative;
`;

const handleStateChange = ({ x, y, scale, angle }) => {
  console.log(scale);
};

const WrapperBack = () => {
  return (
    <Container>
      <PanZoom onStateChange={handleStateChange}>
        <Map>
          {data.map((shoe) => {
            let indivStyle = {
              position: "absolute",
              height: "100px",
              left: `${shoe.x}px`,
              top: `${shoe.y}px`,
            };
            return (
              <img
                className="pic"
                style={indivStyle}
                src={`./shoes/${shoe.name}.jpg`}
              />
            );
          })}
        </Map>
      </PanZoom>
    </Container>
  );
};

export default WrapperBack;

