import React, { useState, setState } from "react";
import "./WrapperBack.css";
import Card from "./Card";
import Bottom from "./Bottom";
import data from "../data.json";
import {
  FaCaretDown,
  FaCaretUp,
  FaCaretLeft,
  FaCaretRight,
} from "react-icons/fa";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import styled from "styled-components";

// const Container = styled.div`
//   height: 550px;
//   width: 300px;
//   display: flex;
//   flex-direction: row;
//   flex-wrap: wrap;
//   align-content: flex-start;
//   overflow: hidden;
//   border: 10px solid green;
//   background: blue;
// `;

const Container = styled.div`
  height: 1000px;
  width: 1000px;
  overflow: hidden;
  background: blue;
  border: 10px solid green;
`;

const Wrapper = styled.div`
  // max-width: 200px;
  // overflow: visible;
  // display: block;
  // border: 10px solid red;
  // height: 800px;
  // width: 800px;
`;

const Map = styled.div`
  width: 500px;
  height: 500px;
  background-color: blue;
  border: 10px solid red;
`;

const WrapperBack = () => {
  return (
    <Wrapper>
      <TransformWrapper
        defaultScale={0.3}
        options={{
          minScale: 0.1,
          // false for centerContent does not seem to work except at initial render?
          // centerContent: false
          // true seems to center it with the TransformComponent-module_container__XXX
          // which causes it to be off-center
          centerContent: true,
        }}
      >
        {({ zoomIn, zoomOut, resetTransform, ...rest }) => (
          <React.Fragment>
            <div style={{ textAlign: "left" }}>
              <div>Orange = Zooming Area; Blue = Object being zoomed</div>
              <button onClick={zoomIn}>+</button>
              <button onClick={zoomOut}>-</button>
              <button onClick={resetTransform}>x</button>
            </div>
            {/* Set zooming area size. Tried with sizing div outside/inside/inside and outside of TransformComponent */}
            <div
              style={{
                height: 510,
                width: 510,
                backgroundColor: "orange",
                border: "10px solid green",
              }}
            >
              <TransformComponent>
                {/* Thing I am actualy trying to zoom. Potentially bigger than TransformComponent parent */}
                <div
                  style={{
                    height: 1000,
                    width: 1000,
                    backgroundColor: "blue",
                    border: "50px solid",
                  }}
                />
              </TransformComponent>
            </div>
          </React.Fragment>
        )}
      </TransformWrapper>
    </Wrapper>
  );
};

export default WrapperBack;

