import React, { useState } from "react";
import { PanZoom } from "react-easy-panzoom";
import { data } from "./data";
import { MdLocationPin } from "react-icons/md";
import "./WrapperBack.css";

const WrapperBack = ( {flipCard} ) => {
  const [zoom, setZoom] = useState(1000);

  const handleStateChange = ({ x, y, scale, angle }) => {
    let newZoom = 1000;
    console.log(x)
    console.log(y)
    console.log(scale)
    if (scale < 0.2) {
      newZoom = 1000;
    }

    if (scale > 0.2 && scale < 0.6) {
      newZoom = 500;
    }

    if (scale > 0.6) {
      newZoom = 100;
    }

    if (newZoom != zoom) {
      setZoom(newZoom);
    }
  };

  let radarStyle = {
    position: "absolute",
    left: `${1000}px`,
    top: `${1000}px`
  };

  return (
    <div className="div-wrapper">
      <PanZoom onStateChange={handleStateChange}>
        <div className="map">
        <a href="#" class="intro-banner-vdo-play-btn pinkBg" style={radarStyle} target="_blank">
        <MdLocationPin className="radar-back" />
          <i class="glyphicon glyphicon-play whiteText" aria-hidden="true"></i>
          <span class="ripple pinkBg"></span>
          <span class="ripple pinkBg"></span>
          <span class="ripple pinkBg"></span>
        </a>
          {data
            .filter((shoe) => zoom == 100 ? true : shoe.zoomClearance == zoom)
            .map((shoe, idx) => {
              console.log(shoe.name)
              console.log((shoe.order % (2000 / zoom)) * zoom)
              let indivStyle = {
                position: "absolute",
                height: `${zoom}px`,
                width: `${zoom}px`,
                left: `${
                  zoom == 100 ? shoe.x : (shoe.order % (2000 / zoom)) * zoom
                }px`,
                top: `${
                  zoom == 100 ? shoe.y : Math.floor(shoe.order / (2000 / zoom)) * zoom
                }px`,
              };
              return (
                <img
                  className="pic"
                  style={indivStyle}
                  src={`./shoes/${shoe.name}.jpg`}
                />
              );
            })}
            <button className="ctrl-btn" onClick={() => flipCard(false)}>Map Similar</button>
        </div>
      </PanZoom>
    </div>
  );
};

export default WrapperBack;

