import React, { useState, setState } from "react";
import "./WrapperBack.css";
import Card from "./Card";
import Bottom from "./Bottom";
import data from "../data.json";
import {
  FaCaretDown,
  FaCaretUp,
  FaCaretLeft,
  FaCaretRight,
} from "react-icons/fa";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";

const WrapperBack = () => {
  const addE = () => {
    var bgPattern = document.getElementById("bg");
    bgPattern.style.width = 100;
    bgPattern.style.backgroundPositionX += 1000;
  };

  return (
    <TransformWrapper>
          {({ zoomIn, zoomOut, resetTransform }) => (
            <React.Fragment>
              <div className="tools">
                <button onClick={zoomIn}>+</button>
                <button onClick={zoomOut}>-</button>
                <button onClick={resetTransform}>x</button>
              </div>
              <TransformComponent>
                <img
                  width="100%"
                  src="https://cdn.pixabay.com/photo/2015/02/24/15/41/dog-647528__340.jpg"
                  alt="test 2"
                />
              </TransformComponent>
            </React.Fragment>
          )}
        </TransformWrapper>
  );
};

export default WrapperBack;

