import React, { useState, setState } from "react";
import { PanZoom } from "react-easy-panzoom";
import styled from "styled-components";
import { data } from "./data";
import "./WrapperBack.css";

// const Container = styled.div`
//   height: 550px;
//   width: 300px;
//   display: flex;
//   flex-direction: row;
//   flex-wrap: wrap;
//   align-content: flex-start;
//   overflow: hidden;
//   border: 10px solid green;
//   background: blue;
// `;

const Container = styled.div`
  height: 550px;
  width: 300px;
  overflow: hidden;
  background: white;
  border: 10px solid green;
`;

const Wrapper = styled.div`
  // max-width: 200px;
  // overflow: visible;
  // display: block;
  // border: 10px solid red;
  // height: 800px;
  // width: 800px;
`;

const Map = styled.div`
  width: 2000px;
  height: 2000px;
  background-color: blue;
  border: 10px solid red;
  position: relative;
`;

const handleStateChange = ({ x, y, scale, angle }) => {
  console.log(scale);
};

const WrapperBack = () => {
  const [zoom, setZoom] = useState(1000);

  const handleStateChange = ({ x, y, scale, angle }) => {
    let newZoom = 1000;
    if (scale < 0.2) {
      newZoom = 1000;
    }

    if (scale > 0.2 && scale < 0.6) {
      newZoom = 500;
    }

    if (scale > 0.6) {
      newZoom = 100;
    }

    if (newZoom != zoom) {
      setZoom(newZoom);
    }
  };

  return (
    <Container>
      <PanZoom onStateChange={handleStateChange}>
        <Map>
          {data
            .filter((shoe) => shoe.zoomClearance >= zoom)
            .map((shoe, idx) => {
              let indivStyle = {
                position: "absolute",
                height: `${zoom}px`,
                width: `${zoom}px`,
                left: `${
                  zoom == 100 ? shoe.x : (idx % (2000 / zoom)) * zoom
                }px`,
                top: `${
                  zoom == 100 ? shoe.y : Math.floor(idx / (2000 / zoom)) * zoom
                }px`,
              };
              return (
                <img
                  className="pic"
                  style={indivStyle}
                  src={`./shoes/${shoe.name}.jpg`}
                />
              );
            })}
        </Map>
      </PanZoom>
    </Container>
  );
};

export default WrapperBack;

