`;

const Wrapper = styled.div`
  max-width: 200px; 
  overflow: visible;
  // display: block;
  // border: 10px solid red;
  // height: 800px;
  // width: 800px;
`;

const Map = styled.div`
  width: 500px;
  height: 500px;
  background-color: blue;
  border: 10px solid red;
`;

const WrapperBack = () => {
  return (
    <Wrapper>
    <TransformWrapper>
    {({ zoomIn, zoomOut, resetTransform }) => (
      <React.Fragment>
        <div className="tools">
          <button onClick={zoomIn}>+</button>
          <button onClick={zoomOut}>-</button>
          <button onClick={resetTransform}>x</button>
        </div>
        <TransformComponent>
          <Map>e</Map>
        </TransformComponent>
      </React.Fragment>
    )}
  </TransformWrapper>
  </Wrapper>
  );
};

export default WrapperBack;

