  border: 10px solid green;
`;

const Wrapper = styled.div`
  // max-width: 200px;
  // overflow: visible;
  // display: block;
  // border: 10px solid red;
  // height: 800px;
  // width: 800px;
`;

const Map = styled.div`
  width: 500px;
  height: 500px;
  background-color: blue;
  border: 10px solid red;
`;

const handleStateChange = ({ x, y, scale, angle }) => {
  console.log(scale)
};

const WrapperBack = () => {
  return (
    <Container>
      <PanZoom
      onStateChange={handleStateChange}
      >
        <Map>{"This content can be panned and zoomed"}</Map>
      </PanZoom>
    </Container>
  );
};

export default WrapperBack;

